import React, { useState } from 'react';
import SimpleForm from './SimpleForm';
import './App.css';

const App = () => {
  const [showChat, setShowChat] = useState(false);
  const [question, setQuestion] = useState('');
  const [response, setResponse] = useState('');

  const startChat = () => setShowChat(true);
  const hideChat = () => setShowChat(false);

  const handleQuestionChange = (e) => setQuestion(e.target.value);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:5000/api/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question }),
      });
      const data = await res.json();
      setResponse(data.response || 'Sin respuesta disponible');
    } catch (error) {
      setResponse('Error al obtener respuesta.');
    }
  };

  return (
    <>
      <div className="header">
        <h2>Asistente Universitario</h2>
      </div>
      <div className="main">
        <div className="nav">
          <h3>Navegaci√≥n</h3>
        </div>
        <div className="content">
          <div style={{ padding: "20px" }}>
            <h1>Bienvenido al Chatbot de ESPE</h1>
            <p>Por favor, use el chatbot para hacer sus preguntas relacionadas con la universidad.</p>
          </div>
        </div>
      </div>
      <div className="footer">Footer</div>
      <div className="bot">
        {showChat && (
          <div className="chat-container">
            <SimpleForm
              question={question}
              handleQuestionChange={handleQuestionChange}
              handleSubmit={handleSubmit}
              response={response}
            />
          </div>
        )}
        <div>
          {!showChat 
            ? <button className="btn" onClick={startChat}>Click para chatear...</button>
            : <button className="btn" onClick={hideChat}>Click para ocultar...</button>}
        </div>
      </div>
    </>
  );
};

export default App;
